private function cmp($a, $b) {
	if (!isset($a['sort_order']) || !isset($b['sort_order'])) return 0;
	if ($a['sort_order'] == $b['sort_order']) return 0;
	return ($a['sort_order'] < $b['sort_order']) ? -1 : 1;
}

// custom inputs
if (isset($this->request->post['custom_inputs'])) {
  foreach($this->request->post['custom_inputs'] as $k => $v) {
    $this->request->post['comment'] = str_replace('{'.$k.'}', $v, $this->request->post['comment']);

    if ($v) {
      $this->request->post['comment'] = str_replace(array('{if_'.$k.'}', '{/if_'.$k.'}'), '', $this->request->post['comment']);
    } else {
      $this->request->post['comment'] = preg_replace('/{if_'.$k.'}(.*){\/if_'.$k.'}/isU', '', $this->request->post['comment']);
    }
  }
}

